const generateInput = (n) => {
    const nums = Array.from({length: n}, (_, i) => i);
    const k = Math.floor(Math.random() * n);
    const rotated = nums.slice(n-k).concat(nums.slice(0, n-k));
    const target = Math.floor(Math.random() * n);
    return [rotated, target];
};
