const generateInput = (n) => {
    const dim = Math.floor(Math.sqrt(n));
    const matrix = [];
    let count = 0;
    for(let r=0; r<dim; r++) {
        const row = [];
        for(let c=0; c<dim; c++) row.push(count++);
        matrix.push(row);
    }
    return [matrix];
};
