const generateInput = (n) => {
    const rows = Math.floor(Math.sqrt(n));
    const cols = Math.floor(n / rows);
    const matrix = [];
    let val = 1;
    for (let i = 0; i < rows; i++) {
        const row = [];
        for (let j = 0; j < cols; j++) {
            row.push(val++);
        }
        matrix.push(row);
    }
    const target = Math.floor(val / 2);
    return [matrix, target];
};
