const generateInput = (n) => {
    const size = Math.floor(Math.sqrt(n));
    const board = [];
    const chars = 'ABC';
    for(let i=0; i<size; i++) {
        const row = [];
        for(let j=0; j<size; j++) row.push(chars[Math.floor(Math.random() * 3)]);
        board.push(row);
    }
    const word = 'ABCA';
    return [board, word];
};
