const generateInput = (n) => {
    const ops = ['LRUCache'];
    const args = [[100]];
    for (let i = 0; i < n; i++) {
        if (Math.random() > 0.5) {
            ops.push('put');
            args.push([i % 100, i]);
        } else {
            ops.push('get');
            args.push([i % 100]);
        }
    }
    return [ops, args];
};
