const generateInput = (n) => {
    const size = Math.floor(Math.sqrt(n));
    const grid = [];
    for(let i=0; i<size; i++) {
        const row = [];
        for(let j=0; j<size; j++) row.push(Math.random() > 0.5 ? "1" : "0");
        grid.push(row);
    }
    return [grid];
};
