const generateInput = (n) => {
    const size = 12;
    const board = [];
    const chars = 'abcdef';
    for(let i=0; i<size; i++) {
        const row = [];
        for(let j=0; j<size; j++) row.push(chars[Math.floor(Math.random() * 6)]);
        board.push(row);
    }
    const words = [];
    for(let i=0; i<n; i++) words.push('abc');
    return [board, words];
};
