const generateInput = (n) => {
    const tickets = [];
    const airports = ['JFK', 'LAX', 'SFO', 'ORD', 'DFW', 'ATL', 'DEN', 'SEA'];
    let from = 'JFK';
    for (let i = 0; i < Math.min(n, 300); i++) {
        const to = airports[(i + 1) % airports.length];
        tickets.push([from, to]);
        from = to;
    }
    return [tickets];
};
