const generateInput = (n) => {
    const size = Math.min(50, Math.floor(Math.sqrt(n)));
    const vals = Array.from({length: size * size}, (_, i) => i);
    vals.sort(() => Math.random() - 0.5);
    const grid = [];
    for (let i = 0; i < size; i++) {
        grid.push(vals.slice(i * size, (i + 1) * size));
    }
    return [grid];
};
