const generateInput = (n) => {
    const groupSize = 3;
    const groups = Math.floor(n / groupSize);
    const hand = [];
    for (let g = 0; g < groups; g++) {
        for (let i = 0; i < groupSize; i++) {
            hand.push(g * groupSize + i);
        }
    }
    return [hand, groupSize];
};
