const generateInput = (n) => {
    const ops = ['TimeMap'];
    const args = [[]];
    for (let i = 0; i < n; i++) {
        ops.push('set');
        args.push(['key' + (i % 10), 'value' + i, i + 1]);
    }
    for (let i = 0; i < n; i++) {
        ops.push('get');
        args.push(['key' + (i % 10), i + 1]);
    }
    return [ops, args];
};
